import React from 'react'

import ImageInfo from '../../../dataTypes/imageInfo'

interface IProps {
  imgInfo: ImageInfo;
}

const ItemChooser: React.FC<IProps> = ({imgInfo}) => {
  //ImageWidth destructuring
  const imgWidth: string = imgInfo.widthPX;
  
  const css = `
    #left {
        clip: rect(0, 100px, 350px, 0);
    }

    #middle {
    left: 0px;
    clip: rect(0, 200px, 350px,100px );
    }

    #right {
    left: 0;
    clip: rect(0, 300px, 350px,200px );
    }
`

  const imgStyle = `
    left: 0;
    clip: rect(0, 300px, 350px,200px );
  `

  return <>
    
    <style>{css}</style>
    <div>
      {imgInfo.showedInfo.map((shwInf, i) => {
      //ShowedInfo destructuring
      //CAMBIAR KEY POR ID
      const [pictureUrl, title]: [string, string] = [shwInf.pictureUrl, shwInf.title];
      
      return <><img 
              key={title} 
              id={"img-item-detail-"+title}
              className="img-item-detail"
              width={imgWidth}
              src={window.location.origin + pictureUrl}
              alt={"Imagen "+title} 
              />
      </>
      })}
    </div>
  </>
}

export default ItemChooser;
