import { useEffect, useState } from 'react';
import { CartProvider } from './context/cartContext';
import Routes from './routes/routes';
import {collection, doc, DocumentData, DocumentReference, Firestore, getDoc, getDocs, getFirestore} from 'firebase/firestore';

import './App.css';

function App() {
  const [products, setProducts] = useState<any>(null);

  useEffect(() => {
    const db: Firestore = getFirestore();
    
    const docRef: DocumentReference<DocumentData> = doc(db,"items","kOZi4LOMN8lxa7piX404")
    

    const itemCollection = collection(db,"items")

    getDocs(itemCollection).then(snapshot => {
      setProducts(snapshot.docs.map(it => {
 
        console.log(it)
      }));
    })
  }, [])
  

  return <div id="app">
  <CartProvider>
    <Routes />
  </CartProvider>
  </div>
}

export default App;
