import React, { useState } from 'react'
import Item from '../dataTypes/item';

import ItemTicket from '../dataTypes/itemTicket';
import { createTicket } from '../helpers/itemFactory';

const CartContext = React.createContext<{items: ItemTicket[],addItem(i: Item, amount: number): void}>
                                       ({items: []          ,addItem: () => {}});


const CartProvider: React.FC<{}> = ({children}) => {
  const [cartItems, setCartItems] = useState<ItemTicket[]>([]);

  const addItem = (newItem: Item, amount: number) => {
    if (cartItems.every(i => i.id !== newItem.id)) {
      try {
        const newTicket = createTicket(newItem, amount);
        setCartItems([...cartItems, newTicket]);
      }
      catch (err: any) {
        if (err instanceof Error) {
          console.warn(err.message);
        }
      }
    }
  }

  return (
    <CartContext.Provider value={{items: cartItems, addItem: addItem}}>
      {children}
    </CartContext.Provider>
  )
}

export {CartProvider,CartContext}
