import { FunctionComponent } from 'react';
import Item from './item';
import {obtenerHelados, obtenerHeladosPromise} from '../../data/helados';
import Helado from '../../dataTypes/helado';

const ItemList: FunctionComponent = () => {
  const onAdd = (num: number, setNum: React.Dispatch<number>): void => setNum(num + 1);
  const onSub = (num: number, setNum: React.Dispatch<number>): void => setNum(num - 1);
  const initial: number = 0;
  const helados: Helado[] = obtenerHelados();
  const heladosPromise: Promise<Helado[]> = obtenerHeladosPromise();

  return <> {
    helados.map(h =>
      <Item 
        key={h.id} 
        item = {h}
        setSelectedItem = {null}
        onClick={()=>{}}
        initial = {initial}
        onAdd = {onAdd}
        onSub = {onSub}
      />
    )}
  </>
}

export default ItemList;
