    const filtroBochaCrema = item => item instanceof HeladoBocha && item.tipoSabor === 'Crema';

    const tipoBochas = ['Paleta','Frutal','Crema'];

    let heladosFiltrados = new Map([
        ['Paleta', helados.filter(filtroPaleta)],
