import ItemShowcase from "./itemShowcase";
import ItemTicket from "./itemTicket";

class Item {
  id: number;
  title: string;
  price: number;
  pictureUrl: string;

  constructor(id: number, title: string, price: number, pictureUrl: string) {
    this.id = id;
    this.title = title;
    this.price = price;
    this.pictureUrl = pictureUrl;
  }

  becomeTicket(amount: number): ItemTicket {
    let res: ItemTicket = new ItemTicket(0,"",0,"",0);

    if (!(this instanceof ItemTicket)) {
      
      //'this' destructuring
      const [id, title, price, pictureUrl]: 
            [number, string, number, string] =
            [this.id, this.title, this.price, this.pictureUrl];
    
      res = new ItemTicket(id, title, price, pictureUrl, amount);
    }
    else {
      res = this;
    }

    if (!(res instanceof ItemTicket)) {
      throw new Error("The returned Item is not an ItemTicket!")
    }
    
    return res;
  }

}

export default Item;
