import React from 'react';
import {Card, Button} from 'react-bootstrap';
import './item.css';

const Item = ({item, setSelectedItem}) => {
  const [imagen, nombre, enStock, descuento] = [item.imagen, item.nombre, item.enStock, item.descuento];
  const selectItem = () => setSelectedItem(item);

  return <>
    <Card style={{ width: '18rem', padding: 0 }}>
  <Card.Img variant="top" src={imagen}/>
  <Card.Body>
    <Card.Title>{nombre}</Card.Title>
    <Card.Text>
      {enStock ? 'En stock':'Sin stock'} 
    </Card.Text>
    <Button className="stretched-link" onClick={selectItem} variant="outline-info" >
      Ver producto
    </Button>
  </Card.Body>
  {
    descuento?
      <Card.Footer>
        <small className="text-muted">{descuento}% de descuento </small>
      </Card.Footer>
      :
      null
  }
</Card>
    <Card href="#helado!" id="carta" style={{ width: '12rem'}} className="">
        <Card.Img className = "" variant="top" src={imagen} />
        <Card.Body id="cuerpo-carta" className = "row d-flex justify-content-center">
            <Card.Title className = "row d-flex justify-content-center ">
              {nombre}
            </Card.Title>
            <Card.Text className = "row d-flex justify-content-center">
              {enStock ? 'En stock':'Sin stock'} 
            </Card.Text>
            
        </Card.Body>
        

    </Card>
  </>;
};

export default Item;
