import Item from './item';
import ItemTicketRecipiente from './itemTicketRecipiente';

class ItemTicket extends Item {
  amount: number;
  
  constructor(id: number, title: string, price: number, pictureUrl: string, amount: number) {
    super(id, title, price, pictureUrl);
    this.amount = amount;
  }

  //The title isnt enough to identify a 'Recipiente' item.
  getTicketTitle(): string {
    let res: string = super.title;

    if (this instanceof ItemTicketRecipiente) {
      this.flavors.forEach((f) => res += " - " + f.title);
    }

    return res;
  } 
}

export default ItemTicket;
