import React, {useState} from 'react';
import {Button} from 'react-bootstrap';
import './itemCount.css'

const ItemCount = ({stock, initial, onAdd}) => {
  const [itemsCarrito, sumaItemsCarrito] = useState(initial);
  const sumar = () => onAdd(itemsCarrito, sumaItemsCarrito);

  return <div>
            <div className="input-group">
                <span onClick={sumar}>
                    <Button>-</Button>
                </span>
                
                    <input type="number" name="cantItems" className="form-control input-number" 
                           defaultValue={initial} min="0" max={stock}>
                        {itemsCarrito}
                    </input>
                <Button>+</Button>
            </div>
            <div className ="input-group py-2 justify-content-center">
                <Button variant="primary">Agregar al carrito</Button>
            </div>
        </div>
        
}

export default ItemCount;
