import Item from "../dataTypes/item";
import ItemTicket from "../dataTypes/itemTicket";

const createTicket = (item: Item, amount: number): ItemTicket => {
  let res: Item = item;
    
  if (!(item instanceof ItemTicket)) {
    //item destructuring
    const [id, title, price, pictureUrl]: 
    [number, string, number, string] =
    [item.id, item.title, item.price, item.pictureUrl];  
    res = new ItemTicket(id, title, price, pictureUrl, amount);
  }

  if (!(res instanceof ItemTicket)) {
      throw new Error("The returned Item is not an ItemTicket!")
  }  

  return res;
}
  
export {createTicket};
