import React from 'react';
import {Card, Button} from 'react-bootstrap';
import './item.css';

const Item = ({item, setSelectedItem}) => {
  const [imagen, nombre, enStock, descuento] = [item.imagen, item.nombre, item.enStock, item.descuento];
  const selectItem = () => setSelectedItem(item);

  return <>
    <Card id="carta" className="" style={{ width: '18rem', padding: 0 }}>
      <Card.Img variant="top" src={imagen}/>
      <Card.Body>
        <Card.Title className="stretched-link" onClick={selectItem}>
          {nombre}
        </Card.Title>
        <Card.Text>
          {enStock ? 'En stock':'Sin stock'} 
        </Card.Text>
      </Card.Body>
      {
        descuento?
          <Card.Footer>
            <small className="text-muted">{descuento}% de descuento </small>
          </Card.Footer>
          :
          null
      }
    </Card>
  </>;
};

export default Item;
