import {collection, doc, Firestore, getDocs, getFirestore} from 'firebase/firestore';
import { stringify } from 'querystring';
import Category from '../dataTypes/category';
import ItemShowcase from '../dataTypes/itemShowcase';
import { ItemHandler } from './item';

class DataLoader {
  private static instance: DataLoader;
  db: Firestore = getFirestore();
  itemCollection = collection(this.db,"items");

  itemHandler: ItemHandler = ItemHandler.getInstance();

  private constructor() { }

  public static getInstance(): DataLoader {
    if (!DataLoader.instance) {
        DataLoader.instance = new DataLoader();
    }
    return DataLoader.instance;
  }

  loadData(): void {
    let data = [];
    getDocs(this.itemCollection).then(snapshot => {
      data = snapshot.docs.map(doc =>
        {
            //Destructuring
            const [id, title, description, price, pictureUrl, category, stock]: 
                  [string, string | undefined, string | undefined, number | undefined, string | undefined, string | undefined, number | undefined] =
                  [doc.id, doc.get("title"), doc.get("description"), doc.get("price"), doc.get("pictureUrl"), doc.get("category"), doc.get("stock")]
            
            if (title === undefined || description === undefined || price === undefined || pictureUrl === undefined || category === undefined || stock === undefined ) {
                console.log(id,title,description,price,pictureUrl,category,stock)
                throw new Error("Parámetros de base de datos erroneos");
            }
            else {
                const newItem = new ItemShowcase(id, title, description, price, pictureUrl, Category.Paleta, stock);
                this.itemHandler.addItem(newItem);
            }
            console.log ({id: doc.id, ...doc.data()})
        }
      )})
        
    //
    /*this.itemHandler.setItems(snapshot.docs.map(doc =>
    ({id: doc.id, ...doc.data()})
    ))
    })*/
    }
}

export default DataLoader;
