import DtItem from '../dataTypes/item';
import {obtenerHelado, obtenerHelados} from '../data/item';

const obtenerPromiseHelados = (setItems: Function): Promise<DtItem[] | void> => {
  const itemsPromise = new Promise<DtItem[]>((resolve,reject) => {
    const itemsDB: DtItem[] = obtenerHelados();
    setTimeout(() => {
      resolve(itemsDB);
    }, 2000);
  })
  .then((result: DtItem[]) => {
    setItems(result);
  })
  .catch((err) => {
    console.error(err);
  });

  return itemsPromise;
}

const getItem = (itemId: number, setItem: Function): Promise<DtItem | void> => {
  const itemsPromise = new Promise<DtItem>((resolve,reject) => {
    const itemsDB: DtItem = obtenerHelado(itemId);
    setTimeout(() => {
      resolve(itemsDB);
    }, 2000);
  })
  .then((result: DtItem) => {
    setItem(result);
  })
  .catch((err) => {
    console.error(err);
  });

  return itemsPromise;
}


export {obtenerPromiseHelados, getItem};
