import React, { useState } from 'react'
import Category from '../dataTypes/category';

import ItemTicket from '../dataTypes/itemTicket';

const CartContext = React.createContext<{
    items: ItemTicket[],
    addItem(it: ItemTicket): void,
    deleteItem(it: ItemTicket): void, 
    deleteAllItems(): void, 
    isInCartById(id: number): boolean,
  }>

  //default values                                    
  ({items: [],
    addItem: (it:ItemTicket)=>{},
    deleteItem: (it:ItemTicket)=>{},
    deleteAllItems: () => {},
    isInCartById: (id: number) => false,
  });


const CartProvider: React.FC<{}> = ({children}) => {
  const [cartItems, setCartItems] = useState<ItemTicket[]>([]);

  const addItem = (newItem: ItemTicket): void => {
    if (cartItems.every(i => i.id !== newItem.id)) {
      setCartItems([...cartItems, newItem]);
    }
  }

  const deleteItem = (deletedItem: ItemTicket): void => {
    setCartItems(cartItems.filter((it: ItemTicket) => !deletedItem.equals(it)));
  }

  const deleteAllItems = (): void => {
    setCartItems([]);
  }

  const isInCartById = (itemId: number): boolean => {
    const searchedItem = cartItems.find((it) => it.id === itemId);
    console.log(cartItems)
    return searchedItem !== undefined && searchedItem.category !== Category.Recipiente;
    //cartItems.some((it: ItemTicket) => it.equals(searchedItem));
  }

  return (
    <CartContext.Provider 
      value={{items: cartItems,
              addItem: addItem, 
              deleteItem:deleteItem,
              deleteAllItems: deleteAllItems, 
              isInCartById: isInCartById}}>
      {children}
    </CartContext.Provider>
  )
}

export {CartProvider,CartContext}
