import { Firestore, getFirestore } from 'firebase/firestore';
import React, { useEffect, useState } from 'react'

const DatabaseContext = React.createContext<{getDatabase: () => Firestore | null}>({ getDatabase: () => null });

const DatabaseProvider: React.FC<{}> = ({children}) => {
  const [db, setDb] = useState<Firestore | null>(null);
  
  const getDatabase = (): Firestore | null => {
    if (db === null) setDb(getFirestore());
    return db;
  }
  
  return (
    <DatabaseContext.Provider 
      value={{getDatabase}}>
      {children}
    </DatabaseContext.Provider>
  )
}

export {DatabaseContext,DatabaseProvider};
