import React, { useContext, useState } from 'react'
import ItemList from '../components/items/list/itemList';
import Item from '../dataTypes/item';

const CartContext = React.createContext('red');

const CartProvider: React.FC<{}> = ({children}) => {
  const [cartItems, setCartItems] = useState<Item[]>([]);

  const addItem = (newItem: Item) => {
    if (cartItems.every(i => i.id !== newItem.id)) {
      setCartItems([...cartItems, newItem]);
    }
  }

  const isDark = useContext(CartContext);
  return (
    <CartContext.Provider value={JSON.stringify(addItem)}>
      {children}
    </CartContext.Provider>
  )
}

export {CartProvider,CartContext}
