          <Col> Cantidad </Col>
          <Col> Precio </Col>
        </Row>
        <Row id="items-cart">
        {items.map((it) => {
          //item destructuring
          const [id, title, pictureUrl, price, amount] : [number, string, string, number, number] =
          [it.id, it.getTicketTitle(), it.pictureUrl, it.price, it.amount];
console.log(it)
          return (
            <Row key={id + title}>
              <Col> <img src={pictureUrl} alt="Item" /> </Col>
              <Col> {title} </Col>
              <Col> {amount} </Col>
              <Col> {price*amount} </Col>
            </Row>
          )
        })}
        </Row>
        <Row id="footer-cart" className="justify-content-start d-flex">
          <Col> Volver </Col>
        </Row>
      </Col>

      
      
    </div>
  )
}

export default Cart;
