import Item from './item';
import ItemCategory from './category';
import ItemTicketRecipiente from './ProductDetailRecipiente';
import ProductDetail from './ProductDetail';

class ItemTicket extends Item {
  amount: number;
  productDetail: ProductDetail;
  
  constructor(id: number, title: string, price: number, pictureUrl: string, category: ItemCategory, productDetail: ProductDetail, amount: number) {
    super(id, title, price, pictureUrl);
    this.productDetail = productDetail;
    this.amount = amount;
  }

  getTicketTitle(): string {
    return this.productDetail.getTicketTitle(this.title);
  }
}

export default ItemTicket;
