import './itemListContainer.css';
import Item from './item.jsx'
import Helado from '../../logica/helado.js';

                /* id, imagen, nombre, tipo, enStock, descuento */
const helados = [new Helado(1,'./default.png','Chocolate'           ,'Crema' ,true ,75),
                 new Helado(2,'./default.png','Dulce de Leche'      ,'Crema' ,false,80),
                 new Helado(3,'./default.png','Frambuesa'           ,'Frutal',true    ),
                 new Helado(4,'./default.png','Lemon Pie'           ,'Crema' ,false   ),
                 new Helado(5,'./default.png','Chocolate c/Almendra','Crema' ,true    ),
                 new Helado(6,'./default.png','Maracuy치'            ,'Frutal',false   ),
                 new Helado(7,'./default.png','Vainilla'            ,'Crema' ,true    ),
                 new Helado(8,'./default.png','Lim칩n'               ,'Frutal',false   ),
                ];

const ItemListContainer = () => {
  /*const [selectedItem, setSelectedItem] = useState(null);*/
/*console.log(selectedItem,setSelectedItem);*/
  return <>
      <div>
        <h1>Lista de productos</h1>
        <h3>Producto seleccionado</h3>
        <Item item = {selectedItem} />
      </div>
      <div className= "row contenedor justify-content-center">
        {helados.map(h =>
           <Item key={h.id} 
            item = {h}
            />
        )}
      </div>
    </>
}

export default ItemListContainer;

