import { Button, Modal } from "react-bootstrap";
import DtItem from "../../../dataTypes/item";

interface IProps {
  items: DtItem[];
  actualItem: DtItem;
  show: boolean;
  onHide: any;
}

const ModalRecipientes: React.FC<IProps> = ({items, actualItem, show,onHide}) => {
    return (
      <Modal
        onHide={onHide}
        show={show}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Modal heading
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>Elige un sabor</h4>
          <div style={{overflowY: "auto"}}>
            {items.map(i => {
                return <>
                    <img key={i.id} width="100px" src={window.location.origin + i.pictureUrl}/>
                    <h1>{i.title}</h1>
                </> 
            })}
            
          </div>
          
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={onHide}>Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }

export default ModalRecipientes;
