import React from 'react';
import {Card, Button} from 'react-bootstrap';
import './item.css';

const Item = ({item, setSelectedItem}) => {
  const [imagen, nombre, enStock] = [item.imagen, item.nombre, item.enStock];
  const selectItem = () => setSelectedItem(item);

  return <>
    <a href="#helado!" className = "col-12">
    <Card id="carta" style={{ width: '10rem'}}>
        <Card.Img className = "" variant="top" src={imagen} />
        <Card.Body className = "row d-flex justify-content-center">
            <Card.Title className = "row d-flex justify-content-center ">
              {nombre}
            </Card.Title>
            <Card.Text className = "row d-flex justify-content-center">
              {enStock ? 'En stock':'Sin stock'} 
            </Card.Text>
            <Button onClick={selectItem} variant="outline-info" >
              Ver producto
            </Button>
        </Card.Body>
    </Card>
    </a>
  </>;
};

export default Item;
