import React, {useState} from 'react';
import './itemListContainer.css';
import Item from './item.jsx'
import Helado from '../../dataTypes/helado.js';

                /*          id|imagen        | nombre               | tipo        |stock|descuento*/
const helados = [new Helado(1,'./default.png','Chocolate'           ,'Paleta'     ,true  ,75),
                 new Helado(2,'./default.png','Dulce de Leche'      ,'Paleta'     ,false ,80),
                 new Helado(3,'./default.png','Frambuesa'           ,'Bocha'      ,true     ),
                 new Helado(4,'./default.png','Lemon Pie'           ,'Recipiente' ,false    ),
                 new Helado(5,'./default.png','Chocolate c/Almendra','Bocha'      ,true     ),
                 new Helado(6,'./default.png','Maracuy치'            ,'Recipiente' ,false    ),
                 new Helado(7,'./default.png','Vainilla'            ,'Recipiente' ,true     ),
                 new Helado(8,'./default.png','Lim칩n'               ,'Paleta'     ,false    ),
                ];

const ItemListContainer = () => {
  const [selectedItem, setSelectedItem] = useState(null);
  
  return <>
    <div id="item-list-container">
      <br />
      <h3>Producto seleccionado</h3>
      <div id="contenedor" className= "row justify-content-center">
        {selectedItem ? 
        <Item
        item = {selectedItem}
        setSelectedItem = {setSelectedItem}
        /> :
        <h1>-</h1>
        }
      </div>
      <h1>Lista de productos</h1>
      <div className= "row contenedor justify-content-center">
        {helados.map(h =>
           <Item key={h.id} 
            item = {h}
            setSelectedItem = {setSelectedItem}
            />
        )}
      </div>
    </div>
  </>
}

export default ItemListContainer;

