import React, { useState } from 'react'

const ModalContext = React.createContext<{
    openModal(mt: React.Dispatch<React.SetStateAction<boolean>>): void,
    closeModal(mt: React.Dispatch<React.SetStateAction<boolean>>): void,
  }>

  //default values                                    
  ({
    openModal: (mt: React.Dispatch<React.SetStateAction<boolean>>) => {},
    closeModal: (mt: React.Dispatch<React.SetStateAction<boolean>>) => {},
  });

//Doesn't let more than one modal to be opened at once
const ModalProvider: React.FC<{}> = ({children}) => {
  interface IOpen {
    opened: boolean;
    setOpened: React.Dispatch<React.SetStateAction<boolean>>;
  }

  //stores the opened state functions
  const openedCollection: React.Dispatch<React.SetStateAction<boolean>>[] = [];

  const closeEverything = () => {
    openedCollection.forEach((setOpen) => {
      setOpen(false);
    });
    while(openedCollection.pop());
  }
  
  const openModal = (setOpenedParam: React.Dispatch<React.SetStateAction<boolean>>) => {
    closeEverything();
    setOpenedParam(true);
    openedCollection.push(setOpenedParam);
  }

  const closeModal = (setOpenedParam: React.Dispatch<React.SetStateAction<boolean>>) => {
    const setOpened = openedCollection.find(set => set == setOpenedParam);
    if (setOpened !== undefined) {
      setOpened(false);
      openedCollection.filter(set => set != setOpened);
    }
  }

  return (
    <ModalContext.Provider 
      value={{openModal: openModal,
              closeModal: closeModal,
             }}>
      {children}
    </ModalContext.Provider>
  )
}

export {ModalContext, ModalProvider}
