import React, { useState } from 'react'
import { obtenerHelados } from '../../../data/item';
import Flavor from '../../../dataTypes/flavor';
import DtItem from '../../../dataTypes/item';
import ItemCategory from '../../../dataTypes/itemCategory';
import { getPromiseFlavors } from '../../../helpers/promises';
import ItemChooser from './itemChooser'

interface IProps {
  numFlavors: number;
}

const ItemChoserContainer: React.FC<IProps> = ({numFlavors}) => {
  const [flavors, setFlavors] = useState<Flavor[]>([]);

  //RECONTRA PROVISORIO
  const items = getPromiseFlavors(setFlavors);
  const [selectedItems, setselectedItems] = useState([flavors[0],flavors[1],items[2]])

  return (
    <ItemChooser 
      imgWidth={500}
      items={items}
      selectedItems={selectedItems}
      setSelectedItems={setselectedItems}
    />
  )
}

export default ItemChoserContainer
