import { Button, Card, Modal } from "react-bootstrap";
import DtItem from "../../../dataTypes/item";

interface IProps {
  items: DtItem[];
  show: boolean;
  onHide: any;
}

const ModalRecipientes: React.FC<IProps> = ({items, show,onHide}) => {
    return (
      <Modal
        onHide={onHide}
        show={show}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Modal heading
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>Elige un sabor</h4>
          <div className="row" style={{overflowY: "auto"}}>
            {items.map(i => {
                return <div key={i.id} style={{height: "10rem"}}>
                    <Card className="item-card" style={{}} >
                        <Card.Img width="100px" variant="top" src={window.location.origin + i.pictureUrl}/>
                    <Card.Body className="body-card">
                        <Card.Title className="item-title btn stretched-link">
                            {i.title}
                        </Card.Title>
                    </Card.Body>
                    </Card>
                </div> 
            })}
            
          </div>
          
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={onHide}>Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }

export default ModalRecipientes;
