import React, { useContext } from 'react'
import { Button, Col, Row } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { CartContext } from '../../context/cartContext';

import './cart.css'
const deleteIcon = require('./delete.png')

const Cart = () => {
  const cartData = useContext(CartContext);
  const items = cartData.items;

  return (
    <Row>
      <Col id="items-cart-container" md="7">
        <Row id="footer-cart" className="py-3">
          <Col sm="2" className="d-flex justify-content-start px-5"> <Link to="/"><Button>Volver</Button></Link> </Col>
          <Col sm="8" className="d-flex justify-content-center px-5"><h2>Carrito</h2></Col>
          <Col sm="2" className="d-flex justify-content-end px-5"> <Button onClick={cartData.deleteAllItems}>Borrar todo</Button> </Col>
        </Row>
        <Row>
          {items.map((it) => {
            //item destructuring
            const [id, title, pictureUrl, price, amount] : 
                  [number, string, string, number, number] =
                  [it.id, it.title, it.pictureUrl, it.price, it.amount];

            return (
              <div className="item-container-cart" key={id + title}>
              <Row className="item-cart">
                <Col className="col-item-cart col-item-img-cart"  sm="3"> <Link to={"/item/"+id}><img width="150px" src={pictureUrl} alt="Item" /></Link> </Col>
                <Col sm="8">
                  <Row className="col-item-cart"> <h5> {title} </h5></Row>
                  <Row className="col-item-cart"> <p> Cantidad: {amount} </p> </Row>
                  <Row className="col-item-cart"> <h5 className="item-price-cart">{price*amount}US$</h5></Row>
                </Col>
                <Col sm="1" className="d-flex">
                  <Row className="col-item-cart"> <span className="delete-icon-cart" onClick={() => cartData.deleteItem(it)}><img width="35px" src={deleteIcon} alt="Borrar" /></span> </Row>
                </Col>
              </Row>
              </div>
            )
          })}
        </Row>
      </Col>
      <Col id="container-cart" md="5">
        <Row id="products-info-cart">
          <Row id="title-cart">
            <Col className="d-flex justify-content-start px-5"> <h1>Resumen</h1> </Col>
            <Col id="amount-items-cart" className="d-flex justify-content-end px-5"> {items.length} items </Col>
          </Row>
          <Row id="column-names-cart">
            <Row> <Col>Subtotal</Col> <Col> cartContext.getTotal()US$ </Col> </Row>
            <hr/>
            <Row> <Col>Descuento</Col> <Col> 0US$ </Col></Row>
            <hr/>
            <Row> <Col>Env√≠o</Col> <Col> Gratis </Col></Row>
            <hr/>
            <Row> <Col>Total</Col> <Col> cartContext.getTotal()US$ </Col></Row>
          </Row>
          <Row><Button>Finalizar Compra</Button></Row>
        </Row>
      </Col>
    </Row>
  )
}

export default Cart;
