import Helado from '../dataTypes/helado';
import {obtenerHelados} from '../data/helados';
import React from 'react';

const obtenerPromiseHelados = (helados: Helado[], setHelados: any): void => {
  const heladosDB: Helado[] = obtenerHelados();
  const heladosPromise = new Promise<Helado[]>((resolve,reject) => {
    if (helados.length === 0) {
      throw new Error('No se han encontrado helados!');
    }
    setTimeout(() => {
      resolve(heladosDB);
    }, 2000);
  }).
  then((result: Helado[]) => {
    setHelados(result);
    console.log('Helados: ' + helados);
  })
  .catch((err) => {
    console.log(err);
  })
  .finally(() => {
    console.log('Fin de promesa de helados!');
  });

}

export {obtenerPromiseHelados};
