import React from 'react';
import {Card, Button} from 'react-bootstrap';
import './item.css';

const Item = ({item, setSelectedItem}) => {
  const [imagen, nombre, enStock] = [item.imagen, item.nombre, item.enStock];
  const selectItem = () => setSelectedItem(item);

  return <>
    <Card href="#helado!" id="carta" style={{ }} className="h-200">
        <Card.Img className = "" variant="top" src={imagen} />
        <Card.Body id="cuerpo-carta" className = "row d-flex justify-content-center">
            <Card.Title className = "row d-flex justify-content-center ">
              {nombre}
            </Card.Title>
            <Card.Text className = "row d-flex justify-content-center">
              {enStock ? 'En stock':'Sin stock'} 
            </Card.Text>
            <Button className="stretched-link" onClick={selectItem} variant="outline-info" >
              Ver producto
            </Button>
        </Card.Body>
    </Card>
  </>;
};

export default Item;
