import 'bootstrap/dist/css/bootstrap.min.css';
import { useEffect, useState } from 'react';
import { idText } from 'typescript';
import './App.css';
import Header from './components/header/header';
import ItemDetailContainer from './components/items/detail/itemDetailContainer';
import ItemListContainer from './components/items/list/itemListContainer';
const logo =  require('./helado.png');

function App() {

  const ruta: string = window.location.href;
  const id: string[] = ruta.split('?');
  const id2: string[] = id[id.length - 1].split('=');
  const id3: string | undefined =
    id2.find((s, i) => {
      return (i-1 >= 0) && (id2[i-1]==='id');
    })

  return (
    <div className="App">
      <ItemListContainer setId={()=>{}} greeting="Bienvenido a Scoop, tu tienda de helados favorita!"/>
      <ItemDetailContainer itemId={id3? parseInt(id3):1} />
    </div>
  );
}

export default App;
