import React, { FunctionComponent, useEffect, useState } from 'react';
import './itemListContainer.css';
import ItemList from './itemList'
import DtItem from '../../../dataTypes/item'
import {obtenerPromiseHelados} from '../../../helpers/promises';
import Loading from '../../loading/loading';
import { useParams } from 'react-router-dom';

interface IProps {
  greeting: string;
}

const ItemListContainer: FunctionComponent<IProps> = ({greeting}: IProps) => {

  //Parameters
  const { category } = useParams<{category?: string}>();
  
  //Showed ice cream
  const [helados,setHelados] = useState<DtItem[]>([]);
  const setFilteredIceCream = (iceCream: DtItem[]) => {
    setHelados(iceCream.filter((ic) => {
      console.log(ic.category,category);
      return String(ic.category) === category;
    }))
  }

  //Gets the ice cream and filters it on mount
  useEffect(() => {
    obtenerPromiseHelados(setFilteredIceCream);
  }, []);
   
  return <>
    <div id="item-list-container">
      <div id="titulo-tienda">
        <h2 id="greeting">{greeting}</h2>
      </div>
      <div id="lista-productos" className= "row">
        <h2>Tienda</h2>
        {helados.length?
         <ItemList items={helados} />:
         <Loading />
        }
        
      </div>
    </div>
  </>
}

export default ItemListContainer;
