import Helado from '../dataTypes/helado';

const helados: Helado[] = [
/*          id|imagen        | nombre               stock|descuento*/
  new Helado(1,'./default.png','Chocolate'           ,10     ),
  new Helado(2,'./default.png','Dulce de Leche'      ,0   ,20),
  new Helado(3,'./default.png','Frambuesa'           ,2      ),
  new Helado(4,'./default.png','Lemon Pie'           ,1      ),
  new Helado(5,'./default.png','Maracuy치'            ,7   ,1 ),
  new Helado(6,'./default.png','Frutilla'            ,16     ),
  new Helado(7,'./default.png','Vainilla'            ,22  ,25),
  new Helado(8,'./default.png','Lim칩n'               ,4      ),
];

const obtenerHelados = (): Helado[] => helados;

const obtenerHeladosPromise = (): Promise<Helado[]> => {
  const heladosPromise = new Promise<Helado[]>((resolve,reject) => {
    if (helados.length === 0) {
      throw new Error('No se han encontrado helados!');
    }
    setTimeout(() => {
      resolve(heladosPromise);
    }, 2000);
  })

  return heladosPromise;
}

export {obtenerHelados, obtenerHeladosPromise};
