import { FunctionComponent } from 'react';
import Item from './item';
import {obtenerHeladosPromise} from '../../data/helados';

const ItemList: FunctionComponent = () => {
  const onAdd = (num: number, setNum: React.Dispatch<number>): void => setNum(num + 1);
  const onSub = (num: number, setNum: React.Dispatch<number>): void => setNum(num - 1);
  const initial: number = 0;
  const helados = obtenerHeladosPromise();
  const heladosPromise = new Promise((resolve,reject) => {
    setTimeout(() => {
      if (helados.length === 0) {
          throw new Error('No se han detectado helados!');
      }

    }, 2000);
  });


  return <> {
    helados.map(h =>
      <Item 
        key={h.id} 
        item = {h}
        setSelectedItem = {null}
        onClick={()=>{}}
        initial = {initial}
        onAdd = {onAdd}
        onSub = {onSub}
      />
    )}
  </>
}

export default ItemList;
