import React from 'react';
import './itemListContainer.css';
import Item from './item.jsx'
import HeladoPaleta from '../../logica/heladoPaleta.js';
import HeladoBocha from '../../logica/heladoBocha.js';

const helados = [new HeladoPaleta(1,'./default.png','Chocolate',true),
                 new HeladoPaleta(2,'./default.png','Dulce de Leche',false),
                 new HeladoBocha(3,'./default.png','Frambuesa',true,'Frutal'),
                 new HeladoBocha(4,'./default.png','Lemon Pie',false,'Crema'),
                ];           

const ItemListContainer = () => {
    const [filtroPaleta, filtroBochaFruta, filtroBochaCrema] = 
                [item => item instanceof HeladoBocha,
                 item instanceof HeladoPaleta && item.tipo === 'Frutal',
                 item instanceof HeladoBocha && item.tipo === 'Crema',
                ]

  return <>
        {helados.map(h => {
            filtroPaleta(h) ?
            <>
            <h1> Paletas </h1>
            <div className= "row contenedor">
                <Item key={h.id} item = {h} />
            </div>
            </> 
            : null
        })}
        {helados.map(h => {
            filtroBochaFruta(h) ?
            <>
            <h1> Helados de Fruta </h1>
            <div className= "row contenedor">
                <Item key={h.id} item = {h} />
            </div> 
            </> 
            : null
        })}
        {helados.map(h => {
            filtroBochaCrema(h) ?
            <>
            <h1> Helados de Crema </h1>
            <div className= "row contenedor">
                <Item key={h.id} item = {h} />
            </div>
            </>  
            : null
        })}
    </>;
}

export default ItemListContainer;
