import React, { useContext } from 'react'
import { Col, Row } from 'react-bootstrap';
import { CartContext } from '../../context/cartContext';

const Cart = () => {
  const cartData = useContext(CartContext);
  const items = cartData.items;

  return (
    <div>
      <Col id="container-cart">
        <Row id="title-cart">
          <Col> Carrito </Col>
          <Col> {items.length} items </Col>
        </Row>
        <Row id="column-names-cart">
          <Col> Producto </Col>
          <Col> TÃ­tulo </Col>
          <Col> Cantidad </Col>
          <Col> Precio </Col>
        </Row>
        <Row id="items-cart">
        {items.map((it) => {
          //item destructuring
          const [id, title, amount] : [number, string, number] =
          [it.id, it.getTicketTitle(), it.amount];

          return (
            <div key={id + title}>
              <h1>{title}</h1>
              <h2>{amount}</h2>
            </div>
          )
        })}
        </Row>
        <Row  id="footer-cart">
          
        </Row>
      </Col>

      
      
    </div>
  )
}

export default Cart;
