import React from 'react';
import {Card} from 'react-bootstrap';
import './item.css';

const Item = ({item, setSelectedItem}) => {
  const [imagen, nombre, enStock, descuento] = [item.imagen, item.nombre, item.enStock, item.descuento];
  const selectItem = () => setSelectedItem(item);

  return <>
  <div className="h-200">
    <Card id="carta" className="" style={{  padding: 0 }}>
      <Card.Img variant="top" src={imagen}/>
      <Card.Body>
        <Card.Title className="btn stretched-link" onClick={selectItem}>
          {nombre}
        </Card.Title>
        <Card.Text>
          {enStock ? 'En stock':'Sin stock'} 
        </Card.Text>
      </Card.Body>
      {descuento?
        <Card.Footer>
          <small className="text-muted">{descuento}% de descuento </small>
        </Card.Footer>
        : null
      }
    </Card>
    </div>
  </>;
};

export default Item;
