import Helado from '../dataTypes/helado';
import {obtenerHelados} from '../data/helados';

const obtenerPromiseHelados = (): Promise<Helado[]> => {
  const helados: Helado[] = obtenerHelados();
  const heladosPromise = new Promise<Helado[]>((resolve,reject) => {
    if (helados.length === 0) {
      throw new Error('No se han encontrado helados!');
    }
    setTimeout(() => {
      resolve(helados);
    }, 2000);
  })
  console.log(heladosPromise);
  return heladosPromise;
}

export {obtenerPromiseHelados};
